// SWIPER




// Clouds animated
if($(window).width() > 1023){

    $('.main').on( "mousemove", function( event ) {

        mX = event.pageX / 8;
        mY = event.pageY / 8;

        var clouds = $('.clouds'),
            cloudTop = clouds.find('.clouds-top'),
            cloudBottom = clouds.find('.clouds-bottom');

        TweenMax.to(cloudTop, 5,{x: - mX,ease:Power2.easeOut});
        TweenMax.to(cloudBottom, 5,{y: - mY,ease:Power2.easeOut});

    });
}



// Slider

$('.featured').slick({
    arrows: false,
    dots: true
});



var swiper = new Swiper('.last__news--wrp', {
    // pagination: '.last__news--arrow',
    paginationClickable: true,
    nextButton: '.last__news--next',
    prevButton: '.last__news--prev',
    slidesPerView: 2,
    // slidesPerColumn: 4,
    spaceBetween: 30,
    slideActiveClass: 'slider-active',
    breakpoints: {
    	767: {
    		slidesPerView: 1,
			spaceBetween: 0
    	}
    }
});



