function loadScript() {
    var script = document.createElement("script");
    script.type = "text/javascript";
    script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyCK0et4nzLjsM6w_ZjONxLZujL6VAS6-sc&callback=initMap";
    document.body.appendChild(script);
}

window.onload = loadScript;


