var mapa = document.querySelector('.places__list');
var li = Array.prototype.slice.call(document.querySelectorAll('li'));
li.forEach(function (el) {

    var dataLatLong = el.dataset.latlong;

    el.addEventListener('click', function () {
        if(mapa.querySelector('.places__active')) {
            mapa.querySelector('.places__active').classList.remove('places__active');
            this.classList.add('places__active');
        }else{
            this.classList.add('places__active');
        }

        if(dataLatLong != 'undefined') {

            var data = dataLatLong.split(',');
            var newPosition = new google.maps.LatLng(data[0], data[1]);

            map.setCenter(new google.maps.LatLng(data[0], data[1]));

            map.setOptions({
                center: newPosition,
                zoom: 15,
                scrollwheel: false,
            });

            var image = 'http://sneakersbr.co/wp-content/themes/simplemag/nmd-assets/img/icone.png';
            var marker = new google.maps.Marker({
                position: newPosition,
                animation: google.maps.Animation.DROP,
                icon: image,
                map: map
            });
        }

        $('.places__list').find('h2 a').removeClass('active');
        $('.places__list').find('h2 a').css({display: 'none'});
        $(el).find('h2 a').css({display: 'inline-block'});
        setTimeout(function(){
            $(el).find('h2 a').addClass('active');
        },250);

    });
});
